 and TEL = #{tel}


 and BANK_HEAD_OFFICE = #{bankHeadOffice}


 and IS_BANK_HEAD = #{isBankHead}


 and BUSINESS_PLACE_CODE = #{businessPlaceCode}


 and IS_YDLW = #{isYdlw}


 and REMARK = #{remark}


 and STATUS = #{status}
