select
hu.*,c.CUSTOMER_NO,c.CUSTOMER_NAME,T.TG_NAME,W.WRITE_SECT_NO,W.WRITE_SECT_NAME
from (
select

from USER_INFO


		and ID = #{id}


		and USER_NO like CONCAT('%',#{userNo},'%')


		and CUSTOMER_ID = #{customerId}


		and USER_NAME like CONCAT('%',#{userName},'%')


		and ADDRESS like CONCAT('%',#{address},'%')


		and BUSINESS_PLACE_CODE = #{businessPlaceCode}


		and WRITE_SECT_ID = #{writeSectId}


		and ALL_CAPACITY = #{allCapacity}


		and LOAD_TYPE = #{loadType}


		and IS_HIGH_TYPE = #{isHighType}


		and TEMP_TYPE = #{tempType}


		and CREDIT_RANK_TYPE = #{creditRankType}


		and WRITE_TYPE = #{writeType}


		and USER_TYPE = #{userType}


		and TG_ID = #{tgId}


		and GUID like CONCAT('%',#{guid},'%')


		METER_BOX_NUMBER = #{meterBoxNumber},


		and CREATE_DATE = #{createDate}


		and REMARK = #{remark}


		and STATUS = #{status}

	AND CUSTOMER_ID IN (SELECT ID FROM CUSTOMER_INFO


			and CUSTOMER_NAME like CONCAT('%',#{customerName},'%')


			and CUSTOMER_NO like CONCAT('%',#{customerNo},'%')


	)

)hu left join CUSTOMER_INFO c on hu.CUSTOMER_ID = c.ID
left join
TG_INFO T on T.ID = hu.TG_ID
LEFT JOIN WRITE_SECTION_INFO W ON
hu.WRITE_SECT_ID =
W.ID
ORDER BY hu.ID DESC

SELECT ID FROM USER_INFO

	CUSTOMER_ID IN

		${item}

select C.ID CUSTOMER_ID,C.CUSTOMER_NO,C.CUSTOMER_NAME,C.CUSTOMER_NAME_SPELL,C.ADDRESS,C.ADDRESS_SPELL,C.CARD_TYPE,C.CARD_NO,C.LINK_MAN,C.CONTACT_INFORMATION, C.CREATE_DATE,C.CANCEL_DATE,C.REMARK,C.STATUS,C.SPARE_LINK_MAN,C.SPARE_CONTACT_INFORMATION,C.BUSINESS_PLACE_CODE,C.WE_CHAT_FLAG,C.INSIDE_FLAG,U.ID ID, U.CUSTOMER_ID,U.USER_NO,U.USER_NAME,U.ADDRESS,U.BUSINESS_PLACE_CODE,U.WRITE_SECT_ID,U.ALL_CAPACITY,U.LOAD_TYPE,U.IS_HIGH_TYPE,U.TEMP_TYPE,U.CREDIT_RANK_TYPE,U.WRITE_TYPE,U.USER_TYPE,U.ELEC_TYPE,U.VOLT_LEVEL_TYPE,U.TG_ID,U.GUID,U.METER_BOX_NUMBER, U.CREATE_DATE,U.REMARK,U.STATUS from CUSTOMER_INFO C left join USER_INFO U on C.ID = U.CUSTOMER_ID
AND C.ID=#{customerId}
AND C.ID IN
${item}

SELECT ID FROM USER_INFO

	WRITE_SECT_ID IN

		${item}

SELECT BUSINESS_PLACE_CODE FROM USER_INFO

	ID IN

		${item}

SELECT

FROM
USER_INFO


		and ID != #{id}


		and USER_NO = #{userNo}


		and STATUS = #{status}
